"use client"

import { useState, useEffect } from "react"

interface Step {
  array: {
    value: number;
    isHighlighted: boolean;
    isWindowStart: boolean;
    isWindowEnd: boolean;
  }[];
  windowSum: number;
  maxSum: number;
}

const SlidingWindow = () => {
  const [array, setArray] = useState([1, 3, -1, -3, 5, 3, 6, 7])
  const [windowSize, setWindowSize] = useState(3)
  const [steps, setSteps] = useState<Step[]>([])
  const [currentStep, setCurrentStep] = useState(0)

  useEffect(() => {
    const calculateSlidingWindow = () => {
      const newSteps = []
      let currentSum = 0
      let maxSum = Number.NEGATIVE_INFINITY

      // Initialize the first window
      for (let i = 0; i < windowSize; i++) {
        currentSum += array[i]
      }
      maxSum = currentSum

      let tempArray = array.map((value, index) => ({
        value: value,
        isHighlighted: index < windowSize,
        isWindowStart: index === 0,
        isWindowEnd: index === windowSize - 1,
      }))

      newSteps.push({
        array: tempArray,
        windowSum: currentSum,
        maxSum: maxSum,
      })

      // Slide the window
      for (let i = 1; i <= array.length - windowSize; i++) {
        currentSum = currentSum - array[i - 1] + array[i + windowSize - 1]
        maxSum = Math.max(maxSum, currentSum)

        tempArray = array.map((value, index) => ({
          value: value,
          isHighlighted: index >= i && index < i + windowSize,
          isWindowStart: index === i,
          isWindowEnd: index === i + windowSize - 1,
        }))

        newSteps.push({
          array: tempArray,
          windowSum: currentSum,
          maxSum: maxSum,
        })
      }

      setSteps(newSteps)
      setCurrentStep(0)
    }

    calculateSlidingWindow()
  }, [array, windowSize])

  const handleNextStep = () => {
    setCurrentStep((prevStep: number) => Math.min(prevStep + 1, steps.length - 1))
  }

  const handlePreviousStep = () => {
    setCurrentStep((prevStep: number) => Math.max(prevStep - 1, 0))
  }

  return (
    <div className="container mx-auto p-8">
      <h1 className="text-2xl font-bold mb-4">Sliding Window Algorithm Visualization</h1>

      <div className="p-6">
        {/* Window Size Input */}
        <div className="mb-6">
          <label className="block mb-2 font-semibold">Window Size:</label>
          <input
            type="number"
            className="border rounded px-3 py-2 w-32"
            value={windowSize}
            min={1}
            max={array.length}
            onChange={(e) => {
              const val = Math.max(1, Math.min(array.length, Number(e.target.value)))
              setWindowSize(val)
            }}
          />
        </div>

        {/* Array Visualization */}
        <div className="flex items-end justify-center gap-4 mb-8">
          {(array ?? []).map((num, idx) => (
            <div
              key={idx}
              className={`w-12 h-12 flex flex-col items-center justify-center rounded shadow
                ${steps[currentStep]?.array?.some(el => el.isHighlighted && el.value === num) ? 'bg-orange-100 border-2 border-orange-400' : 'bg-gray-100'}
              `}
            >
              <span className="text-lg font-bold">{num}</span>
              <span className="text-xs text-gray-500">i={idx}</span>
            </div>
          ))}
        </div>

        {/* Window Analysis */}
        <div className="mt-8 p-6 bg-white rounded shadow border">
          <h2 className="text-xl font-bold mb-4">Window Analysis</h2>
          <div className="grid grid-cols-3 gap-6">
            <div className="bg-orange-50 p-4 rounded text-center">
              <div className="text-2xl font-bold text-orange-600">{steps[currentStep]?.windowSum ?? '-'}</div>
              <div className="mt-2 text-sm">Current Window Sum</div>
            </div>
            <div className="bg-green-50 p-4 rounded text-center">
              <div className="text-2xl font-bold text-green-600">{steps[currentStep].maxSum}</div>
              <div className="mt-2 text-sm">Maximum Sum Found</div>
            </div>
            <div className="bg-blue-50 p-4 rounded text-center">
              <div className="text-2xl font-bold text-blue-600">{windowSize}</div>
              <div className="mt-2 text-sm">Window Size</div>
            </div>
          </div>
        </div>
      </div>

      <div className="mt-4">
        <button
          className="bg-blue-500 hover:bg-blue-700 text-white font-bold py-2 px-4 rounded focus:outline-none focus:shadow-outline mr-2"
          onClick={handlePreviousStep}
          disabled={currentStep === 0}
        >
          Previous
        </button>
        <button
          className="bg-blue-500 hover:bg-blue-700 text-white font-bold py-2 px-4 rounded focus:outline-none focus:shadow-outline"
          onClick={handleNextStep}
          disabled={currentStep === steps.length - 1}
        >
          Next
        </button>
      </div>
    </div>
  )
}

export default SlidingWindow
