import React, { useEffect, useState } from 'react';

interface SortingVisualizerProps {
  algorithm: string;
  inputArray: string;
}

interface Step {
  array: number[];
  description: string;
  code: string;
}

const SortingVisualizer: React.FC<SortingVisualizerProps> = ({ algorithm, inputArray }) => {
  const [steps, setSteps] = useState<Step[]>([]);
  const [currentStep, setCurrentStep] = useState(0);

  useEffect(() => {
    const array = inputArray.split(' ').map(Number);
    const newSteps = generateSteps(algorithm, array);
    setSteps(newSteps);
    setCurrentStep(0);
  }, [algorithm, inputArray]);

  const generateSteps = (algorithm: string, array: number[]): Step[] => {
    // This is a placeholder. We'll implement the actual sorting logic for each algorithm
    const steps: Step[] = [];
    const arr = [...array];

    switch (algorithm) {
      case 'Bubble Sort':
        // Bubble sort implementation
        for (let i = 0; i < arr.length - 1; i++) {
          for (let j = 0; j < arr.length - i - 1; j++) {
            if (arr[j] > arr[j + 1]) {
              [arr[j], arr[j + 1]] = [arr[j + 1], arr[j]];
              steps.push({
                array: [...arr],
                description: `Swapped ${arr[j + 1]} and ${arr[j]}`,
                code: `if (arr[${j}] > arr[${j + 1}]) {
  [arr[${j}], arr[${j + 1}]] = [arr[${j + 1}], arr[${j}]];
}`
              });
            }
          }
        }
        break;
      // Add other sorting algorithms here
      default:
        steps.push({
          array: arr,
          description: 'Algorithm not implemented yet',
          code: '// Implementation coming soon'
        });
    }

    return steps;
  };

  const handleNext = () => {
    if (currentStep < steps.length - 1) {
      setCurrentStep(currentStep + 1);
    }
  };

  const handlePrevious = () => {
    if (currentStep > 0) {
      setCurrentStep(currentStep - 1);
    }
  };

  return (
    <div className="space-y-4">
      <div className="flex items-center justify-between">
        <div className="flex space-x-2">
          {steps[currentStep]?.array.map((value, index) => (
            <div
              key={index}
              className="w-12 h-12 flex items-center justify-center bg-blue-500 text-white rounded-md"
            >
              {value}
            </div>
          ))}
        </div>
        <div className="flex space-x-2">
          <button
            onClick={handlePrevious}
            disabled={currentStep === 0}
            className="px-4 py-2 bg-gray-200 rounded-md disabled:opacity-50"
          >
            Previous
          </button>
          <button
            onClick={handleNext}
            disabled={currentStep === steps.length - 1}
            className="px-4 py-2 bg-blue-500 text-white rounded-md disabled:opacity-50"
          >
            Next
          </button>
        </div>
      </div>

      <div className="bg-gray-50 p-4 rounded-md">
        <h4 className="font-medium text-gray-900 mb-2">Step Description</h4>
        <p className="text-gray-600">{steps[currentStep]?.description}</p>
      </div>

      <div className="bg-gray-50 p-4 rounded-md">
        <h4 className="font-medium text-gray-900 mb-2">Code</h4>
        <pre className="bg-gray-800 text-white p-4 rounded-md overflow-x-auto">
          <code>{steps[currentStep]?.code}</code>
        </pre>
      </div>

      <div className="text-sm text-gray-500">
        Step {currentStep + 1} of {steps.length}
      </div>
    </div>
  );
};

export default SortingVisualizer;
