"use client"

import type React from "react"
import { useState, useEffect } from "react"
import { Link } from "react-router-dom"
import { ArrowLeft, ArrowRight, Plus, Minus } from "lucide-react"

interface ArrayElement {
  value: number
  isHighlighted: boolean
  isPointer1: boolean
  isPointer2: boolean
  isPointer3: boolean
}

function TwoPointerPage() {
  const [array, setArray] = useState<ArrayElement[]>([])
  const [target, setTarget] = useState<number>(0)
  const [operationHistory, setOperationHistory] = useState<string[]>([])
  const [currentStep, setCurrentStep] = useState<number>(0)
  const [isVisualizing, setIsVisualizing] = useState<boolean>(false)
  const [selectedAlgorithm, setSelectedAlgorithm] = useState<"two-sum" | "three-sum">("two-sum")

  useEffect(() => {
    // Load array from localStorage
    const savedArray = localStorage.getItem('currentArray')
    if (savedArray) {
      const numbers = JSON.parse(savedArray)
      setArray(numbers.map((num: number) => ({
        value: num,
        isHighlighted: false,
        isPointer1: false,
        isPointer2: false,
        isPointer3: false
      })))
    }
  }, [])

  const resetVisualization = () => {
    setArray(array.map(el => ({
      ...el,
      isHighlighted: false,
      isPointer1: false,
      isPointer2: false,
      isPointer3: false
    })))
    setOperationHistory([])
    setCurrentStep(0)
    setIsVisualizing(false)
  }

  const visualizeTwoSum = async () => {
    resetVisualization()
    setIsVisualizing(true)
    const sortedArray = [...array].sort((a, b) => a.value - b.value)
    let left = 0
    let right = sortedArray.length - 1

    while (left < right) {
      // Update pointers
      const newArray = sortedArray.map((el, i) => ({
        ...el,
        isPointer1: i === left,
        isPointer2: i === right
      }))
      setArray(newArray)
      setOperationHistory(prev => [...prev, `Checking ${sortedArray[left].value} + ${sortedArray[right].value} = ${sortedArray[left].value + sortedArray[right].value}`])
      await new Promise(resolve => setTimeout(resolve, 1000))

      const sum = sortedArray[left].value + sortedArray[right].value
      if (sum === target) {
        // Found a pair
        const finalArray = newArray.map((el, i) => ({
          ...el,
          isHighlighted: i === left || i === right
        }))
        setArray(finalArray)
        setOperationHistory(prev => [...prev, `Found pair: ${sortedArray[left].value} + ${sortedArray[right].value} = ${target}`])
        break
      } else if (sum < target) {
        left++
      } else {
        right--
      }
    }

    if (left >= right) {
      setOperationHistory(prev => [...prev, "No pair found that sums to target"])
    }
    setIsVisualizing(false)
  }

  const visualizeThreeSum = async () => {
    resetVisualization()
    setIsVisualizing(true)
    const sortedArray = [...array].sort((a, b) => a.value - b.value)

    for (let i = 0; i < sortedArray.length - 2; i++) {
      let left = i + 1
      let right = sortedArray.length - 1

      while (left < right) {
        // Update pointers
        const newArray = sortedArray.map((el, idx) => ({
          ...el,
          isPointer1: idx === i,
          isPointer2: idx === left,
          isPointer3: idx === right
        }))
        setArray(newArray)
        setOperationHistory(prev => [...prev, `Checking ${sortedArray[i].value} + ${sortedArray[left].value} + ${sortedArray[right].value} = ${sortedArray[i].value + sortedArray[left].value + sortedArray[right].value}`])
        await new Promise(resolve => setTimeout(resolve, 1000))

        const sum = sortedArray[i].value + sortedArray[left].value + sortedArray[right].value
        if (sum === target) {
          // Found a triplet
          const finalArray = newArray.map((el, idx) => ({
            ...el,
            isHighlighted: idx === i || idx === left || idx === right
          }))
          setArray(finalArray)
          setOperationHistory(prev => [...prev, `Found triplet: ${sortedArray[i].value} + ${sortedArray[left].value} + ${sortedArray[right].value} = ${target}`])
          return
        } else if (sum < target) {
          left++
        } else {
          right--
        }
      }
    }

    setOperationHistory(prev => [...prev, "No triplet found that sums to target"])
    setIsVisualizing(false)
  }

  return (
    <div className="min-h-screen bg-gradient-to-br from-slate-50 via-white to-slate-100">
      <header className="bg-white shadow-sm border-b">
        <div className="max-w-7xl mx-auto py-6 px-4 sm:px-6 lg:px-8">
          <div className="flex items-center justify-between">
            <div className="flex items-center space-x-3">
              <Link to="/array-algorithms" className="p-2 hover:bg-gray-100 rounded-lg transition-colors">
                <ArrowLeft className="w-6 h-6 text-gray-600" />
              </Link>
              <div className="p-2 bg-gradient-to-r from-blue-500 to-cyan-500 rounded-lg">
                <ArrowRight className="w-6 h-6 text-white" />
              </div>
              <h1 className="text-3xl font-bold bg-gradient-to-r from-blue-600 to-cyan-600 bg-clip-text text-transparent">
                Two Pointer Techniques
              </h1>
            </div>
          </div>
        </div>
      </header>

      <main className="max-w-7xl mx-auto py-8 px-4 sm:px-6 lg:px-8">
        {/* Algorithm Selection */}
        <div className="mb-8 bg-white rounded-2xl shadow-lg border border-gray-200 p-6">
          <h2 className="text-xl font-bold text-gray-900 mb-4">Select Algorithm</h2>
          <div className="flex gap-4">
            <button
              onClick={() => setSelectedAlgorithm("two-sum")}
              className={`px-4 py-2 rounded-lg ${
                selectedAlgorithm === "two-sum"
                  ? "bg-blue-600 text-white"
                  : "bg-gray-100 text-gray-700 hover:bg-gray-200"
              }`}
            >
              Two Sum
            </button>
            <button
              onClick={() => setSelectedAlgorithm("three-sum")}
              className={`px-4 py-2 rounded-lg ${
                selectedAlgorithm === "three-sum"
                  ? "bg-blue-600 text-white"
                  : "bg-gray-100 text-gray-700 hover:bg-gray-200"
              }`}
            >
              Three Sum
            </button>
          </div>
        </div>

        {/* Target Input */}
        <div className="mb-8 bg-white rounded-2xl shadow-lg border border-gray-200 p-6">
          <h2 className="text-xl font-bold text-gray-900 mb-4">Set Target Sum</h2>
          <div className="flex gap-4">
            <input
              type="number"
              value={target}
              onChange={(e) => setTarget(parseInt(e.target.value))}
              className="px-4 py-2 rounded-lg border border-gray-300 focus:ring-2 focus:ring-blue-500"
              placeholder="Enter target sum"
            />
            <button
              onClick={selectedAlgorithm === "two-sum" ? visualizeTwoSum : visualizeThreeSum}
              disabled={isVisualizing}
              className="px-4 py-2 bg-blue-600 text-white rounded-lg hover:bg-blue-700 disabled:opacity-50"
            >
              Visualize
            </button>
          </div>
        </div>

        {/* Array Visualization */}
        <div className="mb-8 bg-white rounded-2xl shadow-lg border border-gray-200 p-6">
          <h2 className="text-xl font-bold text-gray-900 mb-4">Array Visualization</h2>
          <div className="flex flex-wrap gap-4 justify-center">
            {array.map((element, index) => (
              <div
                key={index}
                className={`w-16 h-16 flex items-center justify-center rounded-lg text-lg font-bold transition-all duration-300 ${
                  element.isHighlighted
                    ? "bg-green-500 text-white"
                    : element.isPointer1
                    ? "bg-blue-500 text-white"
                    : element.isPointer2
                    ? "bg-purple-500 text-white"
                    : element.isPointer3
                    ? "bg-orange-500 text-white"
                    : "bg-gray-100 text-gray-700"
                }`}
              >
                {element.value}
              </div>
            ))}
          </div>
        </div>

        {/* Operation History */}
        <div className="bg-white rounded-2xl shadow-lg border border-gray-200 p-6">
          <h2 className="text-xl font-bold text-gray-900 mb-4">Operation History</h2>
          <div className="space-y-2">
            {operationHistory.map((operation, index) => (
              <div
                key={index}
                className="p-3 bg-gray-50 rounded-lg text-gray-700"
              >
                {operation}
              </div>
            ))}
          </div>
        </div>
      </main>
    </div>
  )
}

export default TwoPointerPage
