sourcesContent:
"use client"

import { useState, useEffect } from "react"
import {
  List,
  Plus,
  Minus,
  ArrowRight,
  ArrowLeft,
  RotateCcw,
  Play,
  Pause,
  SkipForward,
  SkipBack,
  Code,
  BookOpen,
  Lightbulb,
  Target,
  Clock,
  Zap,
} from "lucide-react"

// Enhanced Node interface with animation states
interface ListNode {
  id: string
  value: number
  next: ListNode | null
  prev?: ListNode | null
  isHighlighted?: boolean
  isNew?: boolean
  isDeleting?: boolean
  isModifying?: boolean
  animationDelay?: number
}

interface AnimationStep {
  type: "highlight" | "insert" | "delete" | "modify" | "traverse" | "complete"
  nodeId?: string
  position?: number
  description: string
  code: string
  newNode?: ListNode
}

type ListType = "singly" | "doubly" | "circular"
type OperationType =
  | "insert-beginning"
  | "insert-end"
  | "insert-position"
  | "delete-beginning"
  | "delete-end"
  | "delete-position"
  | "modify"
  | "reverse"
  | "search"

function LinkedListVisualizerPage() {
  const [listType, setListType] = useState<ListType>("singly")
  const [head, setHead] = useState<ListNode | null>(null)
  const [inputValue, setInputValue] = useState("")
  const [position, setPosition] = useState(1)
  const [searchTerm, setSearchTerm] = useState("") // Renamed searchValue to searchTerm

  // Animation states
  const [isAnimating, setIsAnimating] = useState(false)
  const [animationSteps, setAnimationSteps] = useState<AnimationStep[]>([])
  const [currentStepIndex, setCurrentStepIndex] = useState(0)
  const [animationSpeed, setAnimationSpeed] = useState(1000)
  const [autoPlay, setAutoPlay] = useState(false)

  // UI states
  const [operationHistory, setOperationHistory] = useState<string[]>([])
  const [showCode, setShowCode] = useState(false)
  const [showTutorial, setShowTutorial] = useState(false)
  const [selectedOperation, setSelectedOperation] = useState<OperationType | null>(null)
  const [nodeCounter, setNodeCounter] = useState(0)

  // Auto-play animation
  useEffect(() => {
    if (autoPlay && isAnimating && currentStepIndex < animationSteps.length - 1) {
      const timer = setTimeout(() => {
        setCurrentStepIndex((prev) => prev + 1)
      }, animationSpeed)
      return () => clearTimeout(timer)
    } else if (currentStepIndex >= animationSteps.length - 1) {
      setAutoPlay(false)
      setIsAnimating(false)
    }
  }, [autoPlay, isAnimating, currentStepIndex, animationSteps.length, animationSpeed])

  // Helper functions
  const generateNodeId = () => {
    setNodeCounter((prev) => prev + 1)
    return `node-${nodeCounter}`
  }

  const createNode = (value: number): ListNode => ({
    id: generateNodeId(),
    value,
    next: null,
    isNew: true,
  })

  const createDoublyNode = (value: number): ListNode => ({
    id: generateNodeId(),
    value,
    next: null,
    prev: null,
    isNew: true,
  })

  const toArray = (): ListNode[] => {
    const arr: ListNode[] = []
    let curr = head
    if (!curr) return arr

    if (listType === "circular") {
      do {
        arr.push(curr)
        curr = curr.next
      } while (curr && curr !== head && arr.length < 20) // Prevent infinite loop
    } else {
      while (curr) {
        arr.push(curr)
        curr = curr.next
      }
    }
    return arr
  }

  const clearAnimationStates = () => {
    const updateNode = (node: ListNode): ListNode => ({
      ...node,
      isHighlighted: false,
      isNew: false,
      isDeleting: false,
      isModifying: false,
      animationDelay: 0,
    })

    const clearStates = (node: ListNode | null): ListNode | null => {
      if (!node) return null
      const updated = updateNode(node)
      if (updated.next && listType !== "circular") {
        updated.next = clearStates(updated.next)
      } else if (updated.next && listType === "circular" && updated.next !== head) {
        updated.next = clearStates(updated.next)
      }
      return updated
    }

    setHead(clearStates(head))
  }

  // Enhanced insertion with animation
  const insertAtBeginning = async (value: number) => {
    const steps: AnimationStep[] = []
    const newNode = listType === "doubly" ? createDoublyNode(value) : createNode(value)

    steps.push({
      type: "highlight",
      description: `Creating new node with value ${value}`,
      code: `const newNode = { value: ${value}, next: null${listType === "doubly" ? ", prev: null" : ""} }`,
    })

    if (!head) {
      if (listType === "circular") {
        newNode.next = newNode
        steps.push({
          type: "insert",
          nodeId: newNode.id,
          description: "List is empty. New node points to itself (circular)",
          code: "newNode.next = newNode\nhead = newNode",
        })
      } else {
        steps.push({
          type: "insert",
          nodeId: newNode.id,
          description: "List is empty. Setting new node as head",
          code: "head = newNode",
        })
      }
    } else {
      if (listType === "doubly") {
        steps.push({
          type: "highlight",
          description: "Setting up doubly linked connections",
          code: "newNode.next = head\nhead.prev = newNode",
        })
        newNode.next = head
        head.prev = newNode
      } else if (listType === "circular") {
        // Find tail for circular
        let tail = head
        while (tail.next && tail.next !== head) {
          tail = tail.next
        }
        steps.push({
          type: "traverse",
          description: "Finding tail node in circular list",
          code: "let tail = head\nwhile (tail.next !== head) tail = tail.next",
        })
        newNode.next = head
        tail.next = newNode
        steps.push({
          type: "insert",
          nodeId: newNode.id,
          description: "Connecting new node and updating tail pointer",
          code: "newNode.next = head\ntail.next = newNode\nhead = newNode",
        })
      } else {
        newNode.next = head
        steps.push({
          type: "insert",
          nodeId: newNode.id,
          description: "Connecting new node to current head",
          code: "newNode.next = head\nhead = newNode",
        })
      }
    }

    steps.push({
      type: "complete",
      description: `Successfully inserted ${value} at the beginning`,
      code: "// Operation completed",
    })

    setAnimationSteps(steps)
    setCurrentStepIndex(0)
    setIsAnimating(true)
    setHead(newNode)

    addToHistory(`Inserted ${value} at beginning`)
  }

  const insertAtEnd = async (value: number) => {
    const steps: AnimationStep[] = []
    const newNode = listType === "doubly" ? createDoublyNode(value) : createNode(value)

    if (!head) {
      return insertAtBeginning(value)
    }

    steps.push({
      type: "highlight",
      description: `Creating new node with value ${value}`,
      code: `const newNode = { value: ${value}, next: null${listType === "doubly" ? ", prev: null" : ""} }`,
    })

    steps.push({
      type: "traverse",
      description: "Traversing to find the last node",
      code: "let curr = head\nwhile (curr.next) curr = curr.next",
    })

    let curr = head
    if (listType === "circular") {
      while (curr.next && curr.next !== head) {
        curr = curr.next
      }
      curr.next = newNode
      newNode.next = head
    } else {
      while (curr.next) {
        curr = curr.next
      }
      curr.next = newNode
      if (listType === "doubly") {
        newNode.prev = curr
      }
    }

    steps.push({
      type: "insert",
      nodeId: newNode.id,
      description: `Connecting new node at the end`,
      code:
        listType === "circular"
          ? "curr.next = newNode\nnewNode.next = head"
          : listType === "doubly"
            ? "curr.next = newNode\nnewNode.prev = curr"
            : "curr.next = newNode",
    })

    steps.push({
      type: "complete",
      description: `Successfully inserted ${value} at the end`,
      code: "// Operation completed",
    })

    setAnimationSteps(steps)
    setCurrentStepIndex(0)
    setIsAnimating(true)
    setHead({ ...head })

    addToHistory(`Inserted ${value} at end`)
  }

  const deleteAtBeginning = async () => {
    if (!head) {
      addToHistory("Cannot delete from empty list")
      return
    }

    const steps: AnimationStep[] = []
    const valueToDelete = head.value

    steps.push({
      type: "highlight",
      nodeId: head.id,
      description: "Highlighting node to be deleted",
      code: "// Deleting head node",
    })

    if (listType === "circular" && head.next === head) {
      steps.push({
        type: "delete",
        nodeId: head.id,
        description: "Only one node in circular list, setting head to null",
        code: "head = null",
      })
      setHead(null)
    } else if (listType === "circular") {
      // Find tail and update its next pointer
      let tail = head
      while (tail.next && tail.next !== head) {
        tail = tail.next
      }
      steps.push({
        type: "traverse",
        description: "Finding tail to update its pointer",
        code: "let tail = head\nwhile (tail.next !== head) tail = tail.next",
      })

      const newHead = head.next
      tail.next = newHead
      steps.push({
        type: "delete",
        nodeId: head.id,
        description: "Updating tail pointer and setting new head",
        code: "tail.next = head.next\nhead = head.next",
      })
      setHead(newHead)
    } else {
      const newHead = head.next
      if (listType === "doubly" && newHead) {
        newHead.prev = null
      }
      steps.push({
        type: "delete",
        nodeId: head.id,
        description: "Setting new head",
        code: listType === "doubly" ? "head = head.next\nif (head) head.prev = null" : "head = head.next",
      })
      setHead(newHead)
    }

    steps.push({
      type: "complete",
      description: `Successfully deleted node with value ${valueToDelete}`,
      code: "// Operation completed",
    })

    setAnimationSteps(steps)
    setCurrentStepIndex(0)
    setIsAnimating(true)

    addToHistory(`Deleted node with value ${valueToDelete} from beginning`)
  }

  const search = async (value: number) => {
    // Renamed function to search
    if (!head) {
      addToHistory("Cannot search in empty list")
      return
    }

    const steps: AnimationStep[] = []
    let curr = head
    let position = 1
    let found = false

    steps.push({
      type: "highlight",
      description: `Searching for value ${value}`,
      code: `let curr = head\nlet position = 1`,
    })

    do {
      steps.push({
        type: "traverse",
        nodeId: curr.id,
        description: `Checking node at position ${position} with value ${curr.value}`,
        code: `if (curr.value === ${value}) return position`,
      })

      if (curr.value === value) {
        found = true
        steps.push({
          type: "highlight",
          nodeId: curr.id,
          description: `Found value ${value} at position ${position}!`,
          code: `// Found at position ${position}`,
        })
        break
      }

      curr = curr.next
      position++
    } while (curr && (listType !== "circular" || curr !== head))

    if (!found) {
      steps.push({
        type: "complete",
        description: `Value ${value} not found in the list`,
        code: "// Value not found",
      })
    }

    setAnimationSteps(steps)
    setCurrentStepIndex(0)
    setIsAnimating(true)

    addToHistory(`Searched for value ${value} - ${found ? `found at position ${position}` : "not found"}`)
  }

  const addToHistory = (operation: string) => {
    setOperationHistory((prev) => [`${new Date().toLocaleTimeString()}: ${operation}`, ...prev.slice(0, 19)])
  }

  // Animation controls
  const playAnimation = () => setAutoPlay(true)
  const pauseAnimation = () => setAutoPlay(false)
  const nextStep = () => {
    if (currentStepIndex < animationSteps.length - 1) {
      setCurrentStepIndex((prev) => prev + 1)
    }
  }
  const prevStep = () => {
    if (currentStepIndex > 0) {
      setCurrentStepIndex((prev) => prev - 1)
    }
  }
  const resetAnimation = () => {
    setCurrentStepIndex(0)
    setAutoPlay(false)
    clearAnimationStates()
  }

  // Get current step info
  const currentStep = animationSteps[currentStepIndex]

  // Tutorial content
  const tutorialContent = {
    singly: {
      title: "Singly Linked List",
      description: "Each node contains data and a pointer to the next node. The last node points to null.",
      complexity: "Insert/Delete at beginning: O(1), Insert/Delete at end: O(n), Search: O(n)",
      useCase: "When you need dynamic size and frequent insertions/deletions at the beginning.",
    },
    doubly: {
      title: "Doubly Linked List",
      description: "Each node contains data and pointers to both next and previous nodes.",
      complexity: "Insert/Delete at beginning/end: O(1), Search: O(n)",
      useCase: "When you need bidirectional traversal and frequent insertions/deletions at both ends.",
    },
    circular: {
      title: "Circular Linked List",
      description: "The last node points back to the first node, forming a circle.",
      complexity: "Insert/Delete at beginning: O(1), Insert/Delete at end: O(n), Search: O(n)",
      useCase: "When you need to cycle through elements repeatedly, like in round-robin scheduling.",
    },
  }

  const nodes = toArray()

  return (
    <div className="min-h-screen bg-gradient-to-br from-slate-50 via-white to-slate-100">
      {/* Header */}
      <header className="bg-white shadow-sm border-b">
        <div className="max-w-7xl mx-auto py-6 px-4 sm:px-6 lg:px-8">
          <div className="flex items-center justify-between">
            <div className="flex items-center space-x-3">
              <div className="p-2 bg-gradient-to-r from-blue-500 to-purple-500 rounded-lg">
                <List className="w-6 h-6 text-white" />
              </div>
              <div>
                <h1 className="text-3xl font-bold bg-gradient-to-r from-blue-600 to-purple-600 bg-clip-text text-transparent">
                  Interactive Linked List Visualizer
                </h1>
                <p className="mt-1 text-gray-600">Learn linked lists through step-by-step animations</p>
              </div>
            </div>
            <div className="flex items-center space-x-2">
              <button
                onClick={() => setShowTutorial(!showTutorial)}
                className="flex items-center space-x-2 px-4 py-2 bg-blue-100 text-blue-700 rounded-lg hover:bg-blue-200 transition-colors"
              >
                <BookOpen className="w-4 h-4" />
                <span>Tutorial</span>
              </button>
              <button
                onClick={() => setShowCode(!showCode)}
                className="flex items-center space-x-2 px-4 py-2 bg-gray-100 text-gray-700 rounded-lg hover:bg-gray-200 transition-colors"
              >
                <Code className="w-4 h-4" />
                <span>Code</span>
              </button>
            </div>
          </div>
        </div>
      </header>

      <main className="max-w-7xl mx-auto py-8 px-4 sm:px-6 lg:px-8">
        {/* Tutorial Panel */}
        {showTutorial && (
          <div className="mb-8 bg-gradient-to-r from-blue-50 to-purple-50 rounded-2xl p-6 border border-blue-200">
            <div className="flex items-center space-x-2 mb-4">
              <Lightbulb className="w-5 h-5 text-blue-600" />
              <h2 className="text-xl font-bold text-blue-900">{tutorialContent[listType].title}</h2>
            </div>
            <p className="text-blue-800 mb-4">{tutorialContent[listType].description}</p>
            <div className="grid grid-cols-1 md:grid-cols-2 gap-4">
              <div className="bg-white rounded-lg p-4">
                <div className="flex items-center space-x-2 mb-2">
                  <Clock className="w-4 h-4 text-green-600" />
                  <span className="font-semibold text-green-800">Time Complexity</span>
                </div>
                <p className="text-sm text-green-700">{tutorialContent[listType].complexity}</p>
              </div>
              <div className="bg-white rounded-lg p-4">
                <div className="flex items-center space-x-2 mb-2">
                  <Target className="w-4 h-4 text-purple-600" />
                  <span className="font-semibold text-purple-800">Use Case</span>
                </div>
                <p className="text-sm text-purple-700">{tutorialContent[listType].useCase}</p>
              </div>
            </div>
          </div>
        )}

        {/* List Type Selector */}
        <div className="mb-8 bg-white rounded-2xl shadow-lg border border-gray-200 p-6">
          <h2 className="text-xl font-bold text-gray-900 mb-4">Choose Linked List Type</h2>
          <div className="grid grid-cols-1 md:grid-cols-3 gap-4">
            {(["singly", "doubly", "circular"] as ListType[]).map((type) => (
              <button
                key={type}
                onClick={() => {
                  setListType(type)
                  setHead(null)
                  setOperationHistory([])
                  setAnimationSteps([])
                  setIsAnimating(false)
                }}
                className={`p-4 rounded-xl border-2 transition-all duration-200 ${
                  listType === type
                    ? "border-blue-500 bg-blue-50 text-blue-700"
                    : "border-gray-200 hover:border-gray-300 hover:bg-gray-50"
                }`}
              >
                <div className="text-lg font-semibold capitalize">{type} Linked List</div>
                <div className="text-sm text-gray-600 mt-1">
                  {type === "singly" && "→ One-way connections"}
                  {type === "doubly" && "↔ Two-way connections"}
                  {type === "circular" && "↻ Circular connections"}
                </div>
              </button>
            ))}
          </div>
        </div>

        <div className="grid grid-cols-1 lg:grid-cols-3 gap-8">
          {/* Controls Panel */}
          <div className="lg:col-span-1">
            <div className="bg-white rounded-2xl shadow-lg border border-gray-200 p-6 sticky top-8">
              <h2 className="text-xl font-bold text-gray-900 mb-6">Operations</h2>

              {/* Input Controls */}
              <div className="space-y-4 mb-6">
                <div>
                  <label className="block text-sm font-medium text-gray-700 mb-2">Value</label>
                  <input
                    type="number"
                    value={inputValue}
                    onChange={(e) => setInputValue(e.target.value)}
                    className="w-full px-3 py-2 border border-gray-300 rounded-lg focus:ring-2 focus:ring-blue-500 focus:border-transparent"
                    placeholder="Enter value"
                  />
                </div>
                <div>
                  <label className="block text-sm font-medium text-gray-700 mb-2">Position</label>
                  <input
                    type="number"
                    value={position}
                    onChange={(e) => setPosition(Number(e.target.value))}
                    min="1"
                    className="w-full px-3 py-2 border border-gray-300 rounded-lg focus:ring-2 focus:ring-blue-500 focus:border-transparent"
                    placeholder="Position"
                  />
                </div>
                <div>
                  <label className="block text-sm font-medium text-gray-700 mb-2">Search Value</label>
                  <input
                    type="number"
                    value={searchTerm}
                    onChange={(e) => setSearchTerm(e.target.value)}
                    className="w-full px-3 py-2 border border-gray-300 rounded-lg focus:ring-2 focus:ring-blue-500 focus:border-transparent"
                    placeholder="Search for value"
                  />
                </div>
              </div>

              {/* Operation Buttons */}
              <div className="space-y-4">
                <div>
                  <h3 className="font-semibold text-gray-700 mb-2">Insert Operations</h3>
                  <div className="grid grid-cols-1 gap-2">
                    <button
                      onClick={() => {
                        const val = Number.parseInt(inputValue)
                        if (!isNaN(val)) {
                          insertAtBeginning(val)
                          setInputValue("")
                        }
                      }}
                      disabled={isAnimating || !inputValue}
                      className="flex items-center justify-center space-x-2 px-4 py-2 bg-blue-600 text-white rounded-lg hover:bg-blue-700 disabled:opacity-50 disabled:cursor-not-allowed transition-colors"
                    >
                      <Plus className="w-4 h-4" />
                      <span>Insert at Beginning</span>
                    </button>
                    <button
                      onClick={() => {
                        const val = Number.parseInt(inputValue)
                        if (!isNaN(val)) {
                          insertAtEnd(val)
                          setInputValue("")
                        }
                      }}
                      disabled={isAnimating || !inputValue}
                      className="flex items-center justify-center space-x-2 px-4 py-2 bg-green-600 text-white rounded-lg hover:bg-green-700 disabled:opacity-50 disabled:cursor-not-allowed transition-colors"
                    >
                      <Plus className="w-4 h-4" />
                      <span>Insert at End</span>
                    </button>
                  </div>
                </div>

                <div>
                  <h3 className="font-semibold text-gray-700 mb-2">Delete Operations</h3>
                  <div className="grid grid-cols-1 gap-2">
                    <button
                      onClick={deleteAtBeginning}
                      disabled={isAnimating || !head}
                      className="flex items-center justify-center space-x-2 px-4 py-2 bg-red-600 text-white rounded-lg hover:bg-red-700 disabled:opacity-50 disabled:cursor-not-allowed transition-colors"
                    >
                      <Minus className="w-4 h-4" />
                      <span>Delete from Beginning</span>
                    </button>
                  </div>
                </div>

                <div>
                  <h3 className="font-semibold text-gray-700 mb-2">Other Operations</h3>
                  <button
                    onClick={() => {
                      const val = Number.parseInt(searchTerm)
                      if (!isNaN(val)) {
                        search(val)
                        setSearchTerm("")
                      }
                    }}
                    disabled={isAnimating || !searchTerm || !head}
                    className="flex items-center justify-center space-x-2 w-full px-4 py-2 bg-purple-600 text-white rounded-lg hover:bg-purple-700 disabled:opacity-50 disabled:cursor-not-allowed transition-colors"
                  >
                    <Target className="w-4 h-4" />
                    <span>Search Value</span>
                  </button>
                </div>
              </div>

              {/* Animation Controls */}
              {animationSteps.length > 0 && (
                <div className="mt-6 pt-6 border-t border-gray-200">
                  <h3 className="font-semibold text-gray-700 mb-4">Animation Controls</h3>
                  <div className="flex items-center justify-between mb-4">
                    <button
                      onClick={prevStep}
                      disabled={currentStepIndex === 0}
                      className="p-2 bg-gray-100 text-gray-600 rounded-lg hover:bg-gray-200 disabled:opacity-50 disabled:cursor-not-allowed"
                    >
                      <SkipBack className="w-4 h-4" />
                    </button>
                    <button
                      onClick={autoPlay ? pauseAnimation : playAnimation}
                      disabled={currentStepIndex >= animationSteps.length - 1}
                      className="p-2 bg-blue-600 text-white rounded-lg hover:bg-blue-700 disabled:opacity-50 disabled:cursor-not-allowed"
                    >
                      {autoPlay ? <Pause className="w-4 h-4" /> : <Play className="w-4 h-4" />}
                    </button>
                    <button
                      onClick={nextStep}
                      disabled={currentStepIndex >= animationSteps.length - 1}
                      className="p-2 bg-gray-100 text-gray-600 rounded-lg hover:bg-gray-200 disabled:opacity-50 disabled:cursor-not-allowed"
                    >
                      <SkipForward className="w-4 h-4" />
                    </button>
                  </div>
                  <div className="text-center text-sm text-gray-600 mb-2">
                    Step {currentStepIndex + 1} of {animationSteps.length}
                  </div>
                  <div className="mb-4">
                    <label className="block text-sm font-medium text-gray-700 mb-2">Animation Speed</label>
                    <input
                      type="range"
                      min="200"
                      max="2000"
                      step="200"
                      value={animationSpeed}
                      onChange={(e) => setAnimationSpeed(Number(e.target.value))}
                      className="w-full"
                    />
                    <div className="flex justify-between text-xs text-gray-500 mt-1">
                      <span>Fast</span>
                      <span>Slow</span>
                    </div>
                  </div>
                  <button
                    onClick={resetAnimation}
                    className="w-full flex items-center justify-center space-x-2 px-4 py-2 bg-gray-600 text-white rounded-lg hover:bg-gray-700 transition-colors"
                  >
                    <RotateCcw className="w-4 h-4" />
                    <span>Reset</span>
                  </button>
                </div>
              )}
            </div>
          </div>

          {/* Visualization Panel */}
          <div className="lg:col-span-2 space-y-8">
            {/* Main Visualization */}
            <div className="bg-white rounded-2xl shadow-lg border border-gray-200 p-6">
              <div className="flex items-center justify-between mb-6">
                <h2 className="text-xl font-bold text-gray-900">
                  {listType.charAt(0).toUpperCase() + listType.slice(1)} Linked List
                </h2>
                <div className="flex items-center space-x-2 text-sm text-gray-600">
                  <span>Nodes: {nodes.length}</span>
                </div>
              </div>

              {/* List Visualization */}
              <div className="min-h-[200px] bg-gradient-to-r from-blue-50 to-purple-50 rounded-xl p-6 overflow-x-auto">
                {nodes.length === 0 ? (
                  <div className="flex items-center justify-center h-32 text-gray-500">
                    <div className="text-center">
                      <List className="w-12 h-12 mx-auto mb-2 opacity-50" />
                      <p>List is empty. Add some nodes to get started!</p>
                    </div>
                  </div>
                ) : (
                  <div className="flex items-center space-x-4">
                    {nodes.map((node, index) => (
                      <div key={node.id} className="flex items-center">
                        {/* Node */}
                        <div
                          className={`relative px-6 py-4 rounded-xl border-2 font-bold text-lg transition-all duration-500 ${
                            currentStep?.nodeId === node.id
                              ? "bg-yellow-200 border-yellow-500 shadow-lg scale-110"
                              : node.isHighlighted
                                ? "bg-blue-200 border-blue-500 shadow-md"
                                : node.isNew
                                  ? "bg-green-200 border-green-500 shadow-md"
                                  : node.isDeleting
                                    ? "bg-red-200 border-red-500 shadow-md"
                                    : listType === "doubly"
                                      ? "bg-purple-100 border-purple-400"
                                      : listType === "circular"
                                        ? "bg-green-100 border-green-400"
                                        : "bg-blue-100 border-blue-400"
                          }`}
                        >
                          {/* Previous pointer for doubly linked list */}
                          {listType === "doubly" && index > 0 && (
                            <ArrowLeft className="absolute -left-6 top-1/2 transform -translate-y-1/2 w-4 h-4 text-purple-500" />
                          )}

                          <span>{node.value}</span>

                          {/* Next pointer */}
                          {(index < nodes.length - 1 || listType === "circular") && (
                            <ArrowRight className="absolute -right-6 top-1/2 transform -translate-y-1/2 w-4 h-4 text-blue-500" />
                          )}
                        </div>

                        {/* Connection line */}
                        {index < nodes.length - 1 && <div className="w-8 h-0.5 bg-blue-400"></div>}

                        {/* Circular connection indicator */}
                        {listType === "circular" && index === nodes.length - 1 && (
                          <div className="flex items-center ml-4">
                            <div className="w-8 h-0.5 bg-green-400"></div>
                            <RotateCcw className="w-6 h-6 text-green-500" />
                            <span className="ml-2 text-sm text-green-700 font-medium">to HEAD</span>
                          </div>
                        )}
                      </div>
                    ))}
                  </div>
                )}
              </div>

              {/* Current Step Information */}
              {currentStep && (
                <div className="mt-6 p-4 bg-gray-50 rounded-lg">
                  <div className="flex items-center space-x-2 mb-2">
                    <Zap className="w-4 h-4 text-blue-600" />
                    <span className="font-semibold text-gray-900">Current Step</span>
                  </div>
                  <p className="text-gray-700 mb-3">{currentStep.description}</p>
                  {showCode && (
                    <pre className="bg-gray-800 text-green-400 p-3 rounded text-sm overflow-x-auto">
                      <code>{currentStep.code}</code>
                    </pre>
                  )}
                </div>
              )}
            </div>

            {/* Operation History */}
            <div className="bg-white rounded-2xl shadow-lg border border-gray-200 p-6">
              <h2 className="text-xl font-bold text-gray-900 mb-4">Operation History</h2>
              <div className="max-h-60 overflow-y-auto space-y-2">
                {operationHistory.length === 0 ? (
                  <p className="text-gray-500 italic">No operations performed yet</p>
                ) : (
                  operationHistory.map((operation, index) => (
                    <div
                      key={index}
                      className="p-3 bg-gray-50 rounded-lg text-sm text-gray-700 border-l-4 border-blue-400"
                    >
                      {operation}
                    </div>
                  ))
                )}
              </div>
            </div>
          </div>
        </div>
      </main>
    </div>
  )
}

export default LinkedListVisualizerPage
