import React, { useState, useEffect } from 'react';
import { Box, Typography, TextField, Button, Paper, Grid } from '@mui/material';
import { ArrowBack, ArrowForward, PlayArrow, Pause } from '@mui/icons-material';
import { useNavigate } from 'react-router-dom';

const KadanesAlgorithmPage = () => {
  const navigate = useNavigate();
  const [array, setArray] = useState<number[]>([]);
  const [inputValue, setInputValue] = useState('');
  const [currentStep, setCurrentStep] = useState(0);
  const [isPlaying, setIsPlaying] = useState(false);
  const [maxSoFar, setMaxSoFar] = useState<number | null>(null);
  const [currentMax, setCurrentMax] = useState<number | null>(null);
  const [highlightedIndices, setHighlightedIndices] = useState<number[]>([]);

  const handleArrayInput = (value: string) => {
    const numbers = value.split(',').map(num => parseInt(num.trim()));
    if (numbers.every(num => !isNaN(num))) {
      setArray(numbers);
      setCurrentStep(0);
      setMaxSoFar(null);
      setCurrentMax(null);
      setHighlightedIndices([]);
    }
  };

  const visualizeStep = (step: number) => {
    if (array.length === 0 || step >= array.length) {
      setHighlightedIndices([]);
      return;
    }

    const currentNum = array[step];
    const newCurrentMax = currentMax === null ? currentNum : Math.max(currentNum, currentMax + currentNum);
    const newMaxSoFar = maxSoFar === null ? newCurrentMax : Math.max(maxSoFar, newCurrentMax);

    setCurrentMax(newCurrentMax);
    setMaxSoFar(newMaxSoFar);
    setHighlightedIndices([step]);
  };

  useEffect(() => {
    if (isPlaying) {
      const timer = setInterval(() => {
        setCurrentStep(prev => {
          if (prev >= array.length - 1) {
            setIsPlaying(false);
            return prev;
          }
          return prev + 1;
        });
      }, 1000);
      return () => clearInterval(timer);
    }
  }, [isPlaying, array.length]);

  useEffect(() => {
    visualizeStep(currentStep);
  }, [currentStep, array]);

  return (
    <Box sx={{ p: 3 }}>
      <Typography variant="h4" gutterBottom>
        Kadane's Algorithm
      </Typography>
      
      <Paper sx={{ p: 2, mb: 3 }}>
        <Typography variant="body1" paragraph>
          Kadane's Algorithm is used to find the maximum sum of a contiguous subarray.
          It maintains two variables: maxSoFar (maximum sum found so far) and currentMax (maximum sum ending at current position).
        </Typography>
      </Paper>

      <Grid container spacing={3}>
        <Grid item xs={12} md={6}>
          <TextField
            fullWidth
            label="Enter array (comma-separated numbers)"
            value={inputValue}
            onChange={(e) => setInputValue(e.target.value)}
            onBlur={(e) => handleArrayInput(e.target.value)}
            margin="normal"
          />
        </Grid>

        <Grid item xs={12}>
          <Box sx={{ display: 'flex', justifyContent: 'center', mb: 2 }}>
            {array.map((num, index) => (
              <Box
                key={index}
                sx={{
                  width: 60,
                  height: 60,
                  border: '2px solid #1976d2',
                  borderRadius: 1,
                  display: 'flex',
                  alignItems: 'center',
                  justifyContent: 'center',
                  mx: 0.5,
                  backgroundColor: highlightedIndices.includes(index) ? '#bbdefb' : 'white',
                  transition: 'background-color 0.3s',
                }}
              >
                <Typography variant="h6">{num}</Typography>
              </Box>
            ))}
          </Box>
        </Grid>

        <Grid item xs={12}>
          <Box sx={{ display: 'flex', justifyContent: 'center', gap: 2, mb: 2 }}>
            <Button
              variant="contained"
              startIcon={<ArrowBack />}
              onClick={() => setCurrentStep(prev => Math.max(0, prev - 1))}
              disabled={currentStep === 0}
            >
              Previous
            </Button>
            <Button
              variant="contained"
              onClick={() => setIsPlaying(!isPlaying)}
              startIcon={isPlaying ? <Pause /> : <PlayArrow />}
            >
              {isPlaying ? 'Pause' : 'Play'}
            </Button>
            <Button
              variant="contained"
              endIcon={<ArrowForward />}
              onClick={() => setCurrentStep(prev => Math.min(array.length - 1, prev + 1))}
              disabled={currentStep === array.length - 1}
            >
              Next
            </Button>
          </Box>
        </Grid>

        <Grid item xs={12}>
          <Paper sx={{ p: 2, bgcolor: '#f5f5f5' }}>
            <Typography variant="h6" gutterBottom>
              Current Maximum: {currentMax !== null ? currentMax : 'N/A'}
            </Typography>
            <Typography variant="h6" gutterBottom>
              Maximum So Far: {maxSoFar !== null ? maxSoFar : 'N/A'}
            </Typography>
            <Typography variant="body2" color="text.secondary">
              Step {currentStep + 1} of {array.length}
            </Typography>
          </Paper>
        </Grid>

        <Grid item xs={12}>
          <Paper sx={{ p: 2, bgcolor: '#f5f5f5' }}>
            <Typography variant="h6" gutterBottom>
              Implementation
            </Typography>
            <pre style={{ margin: 0, whiteSpace: 'pre-wrap' }}>
{`function kadanesAlgorithm(arr: number[]): number {
  let maxSoFar = arr[0];
  let currentMax = arr[0];
  
  for (let i = 1; i < arr.length; i++) {
    currentMax = Math.max(arr[i], currentMax + arr[i]);
    maxSoFar = Math.max(maxSoFar, currentMax);
  }
  
  return maxSoFar;
}`}
            </pre>
          </Paper>
        </Grid>
      </Grid>
    </Box>
  );
};

export default KadanesAlgorithmPage;
