"use client"

import type React from "react"
import { useState } from "react"
import { 
  Calculator, 
  Hash, 
  TrendingUp, 
  Binary, 
  Layers, 
  RotateCcw, 
  List, 
  Grid 
} from "lucide-react"

interface Algorithm {
  name: string
  description: string
  timeComplexity: string
  spaceComplexity: string
  pythonCode: string
}

const algorithms: Algorithm[] = [
  {
    name: "Prefix Sum / Difference Array",
    description: "Efficiently compute range sums and handle range updates using prefix sums and difference arrays.",
    timeComplexity: "O(n) preprocessing, O(1) query",
    spaceComplexity: "O(n)",
    pythonCode: `def prefix_sum(arr):
    n = len(arr)
    prefix = [0] * (n + 1)
    for i in range(n):
        prefix[i + 1] = prefix[i] + arr[i]
    return prefix

def range_sum(prefix, left, right):
    return prefix[right + 1] - prefix[left]`
  },
  {
    name: "Hashing / Frequency Count",
    description: "Use hash maps to count frequencies and solve problems efficiently.",
    timeComplexity: "O(n)",
    spaceComplexity: "O(n)",
    pythonCode: `def frequency_count(arr):
    freq = {}
    for num in arr:
        freq[num] = freq.get(num, 0) + 1
    return freq`
  },
  {
    name: "Kadane's Algorithm",
    description: "Find maximum sum subarray using dynamic programming approach.",
    timeComplexity: "O(n)",
    spaceComplexity: "O(1)",
    pythonCode: `def kadanes_algorithm(arr):
    max_so_far = float('-inf')
    max_ending_here = 0
    
    for num in arr:
        max_ending_here = max(num, max_ending_here + num)
        max_so_far = max(max_so_far, max_ending_here)
    
    return max_so_far`
  },
  {
    name: "Prefix XOR / Bit Manipulation",
    description: "Use XOR operations and prefix XOR arrays for efficient bit manipulation.",
    timeComplexity: "O(n)",
    spaceComplexity: "O(n)",
    pythonCode: `def prefix_xor(arr):
    n = len(arr)
    prefix = [0] * (n + 1)
    for i in range(n):
        prefix[i + 1] = prefix[i] ^ arr[i]
    return prefix`
  },
  {
    name: "Monotonic Stack",
    description: "Use monotonic stacks to find next greater/smaller elements efficiently.",
    timeComplexity: "O(n)",
    spaceComplexity: "O(n)",
    pythonCode: `def next_greater_element(arr):
    n = len(arr)
    result = [-1] * n
    stack = []
    
    for i in range(n):
        while stack and arr[stack[-1]] < arr[i]:
            result[stack.pop()] = arr[i]
        stack.append(i)
    
    return result`
  },
  {
    name: "Rotations and Reversals",
    description: "Efficiently handle array rotations and reversals.",
    timeComplexity: "O(n)",
    spaceComplexity: "O(1)",
    pythonCode: `def rotate_array(arr, k):
    n = len(arr)
    k = k % n
    
    def reverse(start, end):
        while start < end:
            arr[start], arr[end] = arr[end], arr[start]
            start += 1
            end -= 1
    
    reverse(0, n-1)
    reverse(0, k-1)
    reverse(k, n-1)
    
    return arr`
  },
  {
    name: "Subarray / Subsequence Problems",
    description: "Solve problems involving subarrays and subsequences using various techniques.",
    timeComplexity: "Varies",
    spaceComplexity: "Varies",
    pythonCode: `def max_subarray_sum(arr):
    max_sum = float('-inf')
    current_sum = 0
    
    for num in arr:
        current_sum = max(num, current_sum + num)
        max_sum = max(max_sum, current_sum)
    
    return max_sum`
  },
  {
    name: "Two-Dimensional Arrays",
    description: "Handle 2D array operations and matrix problems efficiently.",
    timeComplexity: "Varies",
    spaceComplexity: "Varies",
    pythonCode: `def spiral_order(matrix):
    if not matrix:
        return []
    
    result = []
    top, bottom = 0, len(matrix) - 1
    left, right = 0, len(matrix[0]) - 1
    
    while top <= bottom and left <= right:
        # Traverse right
        for i in range(left, right + 1):
            result.append(matrix[top][i])
        top += 1
        
        # Traverse down
        for i in range(top, bottom + 1):
            result.append(matrix[i][right])
        right -= 1
        
        if top <= bottom:
            # Traverse left
            for i in range(right, left - 1, -1):
                result.append(matrix[bottom][i])
            bottom -= 1
        
        if left <= right:
            # Traverse up
            for i in range(bottom, top - 1, -1):
                result.append(matrix[i][left])
            left += 1
    
    return result`
  }
]

interface ArrayElement {
  value: number
  isHighlighted: boolean
  isPointer?: boolean
  pointerLabel?: string
}

function AdvancedArrayAlgorithmsPage() {
  const [array, setArray] = useState<ArrayElement[]>([])
  const [inputValue, setInputValue] = useState<string>("")
  const [operationHistory, setOperationHistory] = useState<string[]>([])
  const [isAnimating, setIsAnimating] = useState(false)
  const [showCodeMap, setShowCodeMap] = useState<Record<string, boolean>>({})
  const [currentAlgorithm, setCurrentAlgorithm] = useState<string>("")
  const [matrix, setMatrix] = useState<number[][]>([])
  const [matrixSize, setMatrixSize] = useState<string>("3")

  const toggleCodeVisibility = (algorithmName: string) => {
    setShowCodeMap(prev => ({
      ...prev,
      [algorithmName]: !prev[algorithmName]
    }))
  }

  const addNumber = () => {
    if (!inputValue || isNaN(Number(inputValue))) {
      setOperationHistory(prev => [...prev, "Error: Please enter a valid number!"])
      return
    }

    const newElement = { value: Number(inputValue), isHighlighted: false }
    setArray(prev => [...prev, newElement])
    setOperationHistory(prev => [...prev, `Added ${inputValue} to array`])
    setInputValue("")
  }

  const clearArray = () => {
    setArray([])
    setMatrix([])
    setOperationHistory(prev => [...prev, "Array/Matrix cleared"])
  }

  const visualizePrefixSum = () => {
    if (array.length === 0) {
      setOperationHistory(prev => [...prev, "Error: Array is empty!"])
      return
    }

    setIsAnimating(true)
    let prefixSum = 0
    let currentIndex = 0

    const animate = () => {
      if (currentIndex >= array.length) {
        setIsAnimating(false)
        return
      }

      prefixSum += array[currentIndex].value
      
      setArray(prev => prev.map((el, idx) => ({
        ...el,
        isHighlighted: idx <= currentIndex
      })))

      setOperationHistory(prev => [...prev, 
        `Prefix Sum at index ${currentIndex}: ${prefixSum}`
      ])

      currentIndex++
      setTimeout(animate, 1000)
    }

    animate()
  }

  const visualizeKadanes = () => {
    if (array.length === 0) {
      setOperationHistory(prev => [...prev, "Error: Array is empty!"])
      return
    }

    setIsAnimating(true)
    let maxSoFar = -Infinity
    let currentSum = 0
    let currentIndex = 0

    const animate = () => {
      if (currentIndex >= array.length) {
        setIsAnimating(false)
        return
      }

      currentSum = Math.max(array[currentIndex].value, currentSum + array[currentIndex].value)
      maxSoFar = Math.max(maxSoFar, currentSum)
      
      setArray(prev => prev.map((el, idx) => ({
        ...el,
        isHighlighted: idx === currentIndex
      })))

      setOperationHistory(prev => [...prev, 
        `Current Sum: ${currentSum}, Max So Far: ${maxSoFar}`
      ])

      currentIndex++
      setTimeout(animate, 1000)
    }

    animate()
  }

  const createMatrix = () => {
    const size = parseInt(matrixSize)
    if (isNaN(size) || size < 1) {
      setOperationHistory(prev => [...prev, "Error: Please enter a valid matrix size!"])
      return
    }

    const newMatrix = Array(size).fill(0).map(() => Array(size).fill(0))
    setMatrix(newMatrix)
    setOperationHistory(prev => [...prev, `Created ${size}x${size} matrix`])
  }

  const visualizeSpiralOrder = () => {
    if (matrix.length === 0) {
      setOperationHistory(prev => [...prev, "Error: Matrix is empty!"])
      return
    }

    setIsAnimating(true)
    let result: number[] = []
    let top = 0, bottom = matrix.length - 1
    let left = 0, right = matrix[0].length - 1

    const animate = () => {
      if (top > bottom || left > right) {
        setIsAnimating(false)
        return
      }

      // Highlight current elements being processed
      const newMatrix = matrix.map((row, i) => 
        row.map((val, j) => ({
          value: val,
          isHighlighted: 
            (i === top && j >= left && j <= right) ||
            (j === right && i > top && i <= bottom) ||
            (i === bottom && j < right && j >= left) ||
            (j === left && i < bottom && i > top)
        }))
      )

      setMatrix(newMatrix)
      setOperationHistory(prev => [...prev, 
        `Processing spiral layer: top=${top}, bottom=${bottom}, left=${left}, right=${right}`
      ])

      // Move boundaries
      top++
      bottom--
      left++
      right--

      setTimeout(animate, 1000)
    }

    animate()
  }

  return (
    <div className="min-h-screen bg-gradient-to-br from-slate-50 via-white to-slate-100">
      <header className="bg-white shadow-sm border-b">
        <div className="max-w-7xl mx-auto py-6 px-4 sm:px-6 lg:px-8">
          <div className="flex items-center space-x-3">
            <div className="p-2 bg-gradient-to-r from-blue-500 to-purple-500 rounded-lg">
              <Calculator className="w-6 h-6 text-white" />
            </div>
            <h1 className="text-3xl font-bold bg-gradient-to-r from-blue-600 to-purple-600 bg-clip-text text-transparent">
              Advanced Array Algorithms
            </h1>
          </div>
          <p className="mt-2 text-gray-600">Visualize advanced array algorithms step by step</p>
        </div>
      </header>

      <main className="max-w-7xl mx-auto py-8 px-4 sm:px-6 lg:px-8">
        {/* Algorithm Information */}
        <div className="mb-8 grid grid-cols-1 md:grid-cols-2 lg:grid-cols-3 gap-4">
          {algorithms.map((algorithm) => (
            <div
              key={algorithm.name}
              className="bg-white rounded-lg shadow-md p-4 border border-gray-200"
            >
              <h3 className="text-lg font-semibold text-gray-900 mb-2 flex justify-between items-center">
                {algorithm.name}
                <button 
                  onClick={() => toggleCodeVisibility(algorithm.name)}
                  className="text-blue-500 hover:text-blue-700 text-sm"
                >
                  {showCodeMap[algorithm.name] ? "Hide Code" : "Show Code"}
                </button>
              </h3>
              <p className="text-gray-600 mb-2">{algorithm.description}</p>
              <div className="text-sm text-gray-500">
                <div>Time Complexity: {algorithm.timeComplexity}</div>
                <div>Space Complexity: {algorithm.spaceComplexity}</div>
              </div>
              {showCodeMap[algorithm.name] && (
                <div className="mt-4 bg-gray-100 p-3 rounded-md overflow-x-auto">
                  <pre><code className="lang-python text-sm">{algorithm.pythonCode}</code></pre>
                </div>
              )}
            </div>
          ))}
        </div>

        {/* Array/Matrix Visualization */}
        <div className="grid grid-cols-1 lg:grid-cols-2 gap-8">
          <div className="bg-white rounded-2xl shadow-lg border border-gray-200 p-6">
            <h2 className="text-xl font-bold text-gray-900 mb-4">Array/Matrix Visualization</h2>
            
            <div className="mb-4">
              <label htmlFor="input-value" className="block text-sm font-medium text-gray-700 mb-2">
                Add Number to Array
              </label>
              <div className="flex gap-2">
                <input
                  id="input-value"
                  type="number"
                  value={inputValue}
                  onChange={(e) => setInputValue(e.target.value)}
                  className="flex-1 px-4 py-2 rounded-lg border border-gray-300 focus:ring-2 focus:ring-blue-500"
                  placeholder="Enter a number"
                />
                <button
                  onClick={addNumber}
                  className="px-4 py-2 bg-blue-600 text-white rounded-lg hover:bg-blue-700"
                >
                  Add
                </button>
                <button
                  onClick={clearArray}
                  className="px-4 py-2 bg-red-600 text-white rounded-lg hover:bg-red-700"
                >
                  Clear
                </button>
              </div>
            </div>

            <div className="mb-4">
              <label htmlFor="matrix-size" className="block text-sm font-medium text-gray-700 mb-2">
                Matrix Size
              </label>
              <div className="flex gap-2">
                <input
                  id="matrix-size"
                  type="number"
                  value={matrixSize}
                  onChange={(e) => setMatrixSize(e.target.value)}
                  className="flex-1 px-4 py-2 rounded-lg border border-gray-300 focus:ring-2 focus:ring-blue-500"
                  min="1"
                />
                <button
                  onClick={createMatrix}
                  className="px-4 py-2 bg-green-600 text-white rounded-lg hover:bg-green-700"
                >
                  Create Matrix
                </button>
              </div>
            </div>

            <div className="flex flex-wrap gap-2 mb-6">
              <button
                onClick={visualizePrefixSum}
                disabled={isAnimating}
                className="px-4 py-2 bg-blue-600 text-white rounded-lg hover:bg-blue-700 disabled:opacity-50"
              >
                <Calculator className="w-5 h-5 inline mr-1" />
                Prefix Sum
              </button>
              <button
                onClick={visualizeKadanes}
                disabled={isAnimating}
                className="px-4 py-2 bg-green-600 text-white rounded-lg hover:bg-green-700 disabled:opacity-50"
              >
                <TrendingUp className="w-5 h-5 inline mr-1" />
                Kadane's
              </button>
              <button
                onClick={visualizeSpiralOrder}
                disabled={isAnimating}
                className="px-4 py-2 bg-purple-600 text-white rounded-lg hover:bg-purple-700 disabled:opacity-50"
              >
                <Grid className="w-5 h-5 inline mr-1" />
                Spiral Order
              </button>
            </div>

            {/* Array Display */}
            <div className="border-2 border-gray-300 rounded-lg p-4 min-h-[200px] flex flex-wrap gap-2 items-center">
              {array.map((element, index) => (
                <div
                  key={index}
                  className={`relative w-12 h-12 rounded-lg flex-shrink-0 flex items-center justify-center text-white font-bold transition-all duration-300 ${
                    element.isHighlighted
                      ? "bg-green-500 transform scale-105"
                      : "bg-blue-500"
                  }`}
                >
                  {element.value}
                  {element.isPointer && (
                    <div className="absolute -top-6 left-1/2 transform -translate-x-1/2 text-black font-bold">
                      {element.pointerLabel}
                    </div>
                  )}
                </div>
              ))}
              {array.length === 0 && (
                <div className="text-gray-400 text-center my-auto w-full">Array is empty</div>
              )}
            </div>

            {/* Matrix Display */}
            {matrix.length > 0 && (
              <div className="mt-4 border-2 border-gray-300 rounded-lg p-4">
                <h3 className="text-lg font-semibold mb-2">Matrix</h3>
                <div className="grid gap-2" style={{ gridTemplateColumns: `repeat(${matrix[0].length}, 1fr)` }}>
                  {matrix.map((row, i) =>
                    row.map((cell, j) => (
                      <div
                        key={`${i}-${j}`}
                        className={`w-12 h-12 rounded-lg flex items-center justify-center text-white font-bold transition-all duration-300 ${
                          cell.isHighlighted
                            ? "bg-green-500 transform scale-105"
                            : "bg-blue-500"
                        }`}
                      >
                        {cell.value}
                      </div>
                    ))
                  )}
                </div>
              </div>
            )}
          </div>

          {/* Operation History */}
          <div className="bg-white rounded-2xl shadow-lg border border-gray-200 p-6">
            <h2 className="text-xl font-bold text-gray-900 mb-4">Operation History</h2>
            <div className="h-[500px] overflow-y-auto border rounded-lg p-4">
              {operationHistory.map((operation, index) => (
                <div
                  key={index}
                  className={`p-2 mb-2 rounded-lg ${
                    operation.startsWith("Error")
                      ? "bg-red-100 text-red-700"
                      : "bg-gray-100 text-gray-700"
                  }`}
                >
                  {operation}
                </div>
              ))}
            </div>
          </div>
        </div>
      </main>
    </div>
  )
}

export default AdvancedArrayAlgorithmsPage
