import React, { useState } from 'react';
import { BeakerIcon, CodeBracketIcon } from '@heroicons/react/24/outline';
import SortingVisualizer from './components/SortingVisualizer';

interface SortingAlgorithm {
  name: string;
  description: string;
  timeComplexity: string;
  spaceComplexity: string;
}

const sortingAlgorithms: SortingAlgorithm[] = [
  {
    name: 'Bubble Sort',
    description: 'A simple sorting algorithm that repeatedly steps through the list, compares adjacent elements and swaps them if they are in the wrong order.',
    timeComplexity: 'O(n²)',
    spaceComplexity: 'O(1)'
  },
  {
    name: 'Selection Sort',
    description: 'A simple sorting algorithm that divides the input into a sorted and unsorted region, and repeatedly selects the smallest element from the unsorted region.',
    timeComplexity: 'O(n²)',
    spaceComplexity: 'O(1)'
  },
  {
    name: 'Insertion Sort',
    description: 'A simple sorting algorithm that builds the final sorted array one item at a time by comparing each new item with the already sorted portion.',
    timeComplexity: 'O(n²)',
    spaceComplexity: 'O(1)'
  },
  {
    name: 'Merge Sort',
    description: 'A divide-and-conquer algorithm that recursively breaks down the problem into smaller subproblems until they become simple enough to solve directly.',
    timeComplexity: 'O(n log n)',
    spaceComplexity: 'O(n)'
  },
  {
    name: 'Quick Sort',
    description: 'A divide-and-conquer algorithm that picks an element as pivot and partitions the array around the pivot.',
    timeComplexity: 'O(n log n)',
    spaceComplexity: 'O(log n)'
  },
  {
    name: 'Heap Sort',
    description: 'A comparison-based sorting algorithm that uses a binary heap data structure to sort elements.',
    timeComplexity: 'O(n log n)',
    spaceComplexity: 'O(1)'
  }
];

function App() {
  const [selectedAlgorithm, setSelectedAlgorithm] = useState<SortingAlgorithm | null>(null);
  const [inputArray, setInputArray] = useState<string>('');
  const [showVisualization, setShowVisualization] = useState(false);

  const handleAlgorithmSelect = (algorithm: SortingAlgorithm) => {
    setSelectedAlgorithm(algorithm);
    setShowVisualization(false);
  };

  const handleInputSubmit = (e: React.FormEvent) => {
    e.preventDefault();
    if (inputArray.trim()) {
      setShowVisualization(true);
    }
  };

  return (
    <div className="min-h-screen bg-gray-100">
      <header className="bg-white shadow">
        <div className="max-w-7xl mx-auto py-6 px-4 sm:px-6 lg:px-8">
          <h1 className="text-3xl font-bold text-gray-900">Sorting Algorithms Visualizer</h1>
        </div>
      </header>

      <main className="max-w-7xl mx-auto py-6 sm:px-6 lg:px-8">
        <div className="px-4 py-6 sm:px-0">
          <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-3 gap-4">
            {sortingAlgorithms.map((algorithm) => (
              <div
                key={algorithm.name}
                className={`bg-white overflow-hidden shadow rounded-lg cursor-pointer transition-all duration-200 ${
                  selectedAlgorithm?.name === algorithm.name ? 'ring-2 ring-blue-500' : ''
                }`}
                onClick={() => handleAlgorithmSelect(algorithm)}
              >
                <div className="px-4 py-5 sm:p-6">
                  <h3 className="text-lg font-medium text-gray-900">{algorithm.name}</h3>
                  <p className="mt-1 text-sm text-gray-500">{algorithm.description}</p>
                  <div className="mt-4 flex items-center space-x-4">
                    <div className="flex items-center">
                      <BeakerIcon className="h-5 w-5 text-gray-400" />
                      <span className="ml-2 text-sm text-gray-500">
                        Time: {algorithm.timeComplexity}
                      </span>
                    </div>
                    <div className="flex items-center">
                      <CodeBracketIcon className="h-5 w-5 text-gray-400" />
                      <span className="ml-2 text-sm text-gray-500">
                        Space: {algorithm.spaceComplexity}
                      </span>
                    </div>
                  </div>
                </div>
              </div>
            ))}
          </div>

          {selectedAlgorithm && (
            <div className="mt-8 bg-white shadow sm:rounded-lg">
              <div className="px-4 py-5 sm:p-6">
                <h3 className="text-lg font-medium text-gray-900">
                  Visualize {selectedAlgorithm.name}
                </h3>
                <form onSubmit={handleInputSubmit} className="mt-4">
                  <div className="flex space-x-4">
                    <input
                      type="text"
                      value={inputArray}
                      onChange={(e) => setInputArray(e.target.value)}
                      placeholder="Enter array (e.g., 64 34 25 12 22 11 90)"
                      className="flex-1 min-w-0 block w-full px-3 py-2 rounded-md border border-gray-300 shadow-sm focus:ring-blue-500 focus:border-blue-500 sm:text-sm"
                    />
                    <button
                      type="submit"
                      className="inline-flex items-center px-4 py-2 border border-transparent text-sm font-medium rounded-md shadow-sm text-white bg-blue-600 hover:bg-blue-700 focus:outline-none focus:ring-2 focus:ring-offset-2 focus:ring-blue-500"
                    >
                      Visualize
                    </button>
                  </div>
                </form>

                {showVisualization && (
                  <div className="mt-6">
                    <SortingVisualizer
                      algorithm={selectedAlgorithm.name}
                      inputArray={inputArray}
                    />
                  </div>
                )}
              </div>
            </div>
          )}
        </div>
      </main>
    </div>
  );
}

export default App;
