import React, { useState, useEffect } from 'react';
import { useNavigate } from 'react-router-dom';
import { ArrowLeft, ArrowRight, Play, Pause } from 'lucide-react';

const KadanesAlgorithmPage = () => {
  const navigate = useNavigate();
  const [array, setArray] = useState<number[]>([]);
  const [inputValue, setInputValue] = useState('');
  const [currentStep, setCurrentStep] = useState(0);
  const [isPlaying, setIsPlaying] = useState(false);
  const [maxSoFar, setMaxSoFar] = useState<number | null>(null);
  const [currentMax, setCurrentMax] = useState<number | null>(null);
  const [highlightedIndices, setHighlightedIndices] = useState<number[]>([]);

  const handleArrayInput = (value: string) => {
    const numbers = value.split(',').map(num => parseInt(num.trim()));
    if (numbers.every(num => !isNaN(num))) {
      setArray(numbers);
      setCurrentStep(0);
      setMaxSoFar(null);
      setCurrentMax(null);
      setHighlightedIndices([]);
    }
  };

  const visualizeStep = (step: number) => {
    if (array.length === 0 || step >= array.length) {
      setHighlightedIndices([]);
      return;
    }

    const currentNum = array[step];
    const newCurrentMax = currentMax === null ? currentNum : Math.max(currentNum, currentMax + currentNum);
    const newMaxSoFar = maxSoFar === null ? newCurrentMax : Math.max(maxSoFar, newCurrentMax);

    setCurrentMax(newCurrentMax);
    setMaxSoFar(newMaxSoFar);
    setHighlightedIndices([step]);
  };

  useEffect(() => {
    if (isPlaying) {
      const timer = setInterval(() => {
        setCurrentStep(prev => {
          if (prev >= array.length - 1) {
            setIsPlaying(false);
            return prev;
          }
          return prev + 1;
        });
      }, 1000);
      return () => clearInterval(timer);
    }
  }, [isPlaying, array.length]);

  useEffect(() => {
    visualizeStep(currentStep);
  }, [currentStep, array]);

  return (
    <div className="p-6">
      <h1 className="text-3xl font-bold mb-6">Kadane's Algorithm</h1>
      
      <div className="bg-white rounded-lg shadow-md p-6 mb-6">
        <p className="text-gray-700 mb-4">
          Kadane's Algorithm is used to find the maximum sum of a contiguous subarray.
          It maintains two variables: maxSoFar (maximum sum found so far) and currentMax (maximum sum ending at current position).
        </p>
      </div>

      <div className="grid grid-cols-1 md:grid-cols-2 gap-6">
        <div>
          <input
            type="text"
            value={inputValue}
            onChange={(e) => setInputValue(e.target.value)}
            onBlur={(e) => handleArrayInput(e.target.value)}
            placeholder="Enter array (comma-separated numbers)"
            className="w-full px-4 py-2 border border-gray-300 rounded-lg focus:ring-2 focus:ring-blue-500 focus:border-blue-500"
          />
        </div>

        <div className="flex justify-center">
          {array.map((num, index) => (
            <div
              key={index}
              className={`w-16 h-16 border-2 border-blue-500 rounded-lg flex items-center justify-center mx-1 transition-colors ${
                highlightedIndices.includes(index) ? 'bg-blue-100' : 'bg-white'
              }`}
            >
              <span className="text-xl font-semibold">{num}</span>
            </div>
          ))}
        </div>

        <div className="flex justify-center space-x-4">
          <button
            onClick={() => setCurrentStep(prev => Math.max(0, prev - 1))}
            disabled={currentStep === 0}
            className="px-4 py-2 bg-blue-500 text-white rounded-lg hover:bg-blue-600 disabled:opacity-50 disabled:cursor-not-allowed flex items-center"
          >
            <ArrowLeft className="w-5 h-5 mr-2" />
            Previous
          </button>
          <button
            onClick={() => setIsPlaying(!isPlaying)}
            className="px-4 py-2 bg-blue-500 text-white rounded-lg hover:bg-blue-600 flex items-center"
          >
            {isPlaying ? (
              <>
                <Pause className="w-5 h-5 mr-2" />
                Pause
              </>
            ) : (
              <>
                <Play className="w-5 h-5 mr-2" />
                Play
              </>
            )}
          </button>
          <button
            onClick={() => setCurrentStep(prev => Math.min(array.length - 1, prev + 1))}
            disabled={currentStep === array.length - 1}
            className="px-4 py-2 bg-blue-500 text-white rounded-lg hover:bg-blue-600 disabled:opacity-50 disabled:cursor-not-allowed flex items-center"
          >
            Next
            <ArrowRight className="w-5 h-5 ml-2" />
          </button>
        </div>

        <div className="bg-gray-50 rounded-lg p-6">
          <h2 className="text-xl font-semibold mb-4">Current State</h2>
          <div className="space-y-2">
            <p className="text-lg">
              Current Maximum: <span className="font-semibold">{currentMax !== null ? currentMax : 'N/A'}</span>
            </p>
            <p className="text-lg">
              Maximum So Far: <span className="font-semibold">{maxSoFar !== null ? maxSoFar : 'N/A'}</span>
            </p>
            <p className="text-sm text-gray-500">
              Step {currentStep + 1} of {array.length}
            </p>
          </div>
        </div>

        <div className="bg-gray-50 rounded-lg p-6">
          <h2 className="text-xl font-semibold mb-4">Implementation</h2>
          <pre className="bg-gray-800 text-white p-4 rounded-lg overflow-x-auto">
{`function kadanesAlgorithm(arr: number[]): number {
  let maxSoFar = arr[0];
  let currentMax = arr[0];
  
  for (let i = 1; i < arr.length; i++) {
    currentMax = Math.max(arr[i], currentMax + arr[i]);
    maxSoFar = Math.max(maxSoFar, currentMax);
  }
  
  return maxSoFar;
}`}
          </pre>
        </div>
      </div>
    </div>
  );
};

export default KadanesAlgorithmPage;
